# Define the initial product
product <- c("Feature A", "Feature B")

# Define a function to simulate user feedback on the product
get_user_feedback <- function(product) {
  feedback <- sample(c("Positive", "Neutral", "Negative"), 1)
  print(paste("Received feedback:", feedback))
  return(feedback)
}

# Define a function to add a new feature to the product based on user feedback
add_feature <- function(product, feedback) {
  if(feedback == "Positive") {
    new_feature <- sample(c("Feature C", "Feature D", "Feature E"), 1)
    product <- c(product, new_feature)
    print(paste("Added new feature:", new_feature))
  }
  return(product)
}

# Iterate and improve the product based on user feedback
feedback <- "Positive"
while(feedback != "Negative") {
  feedback <- get_user_feedback(product)
  product <- add_feature(product, feedback)
}

